<meta name="twitter:card" content="summary_large_image" inertia />

@if (seo('twitter.creator'))
    <meta name="twitter:creator" content="@seo('twitter.creator')" inertia />
@endif

@if (seo('twitter.site'))
    <meta name="twitter:site" content="@seo('twitter.site')" inertia />
@endif

@if (seo('twitter.title'))
    <meta name="twitter:title" content="@seo('twitter.title')" inertia />
@endif

@if (seo('twitter.description'))
    <meta name="twitter:description" content="@seo('twitter.description')" inertia />
@endif

@if (seo('twitter.image'))
    <meta name="twitter:image" content="@seo('twitter.image')" inertia />
@endif
